  `type` varchar(20) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT 'MARKET' COMMENT '订单类型 MALL 网络商城订单  MARKET便利店订单  HOTEL餐饮订单',
  `order_product_type` varchar(32) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '订单里面商品的类型 COUNT 、GROUP_BUY',
  `distribute` tinyint(2) UNSIGNED NULL DEFAULT 0 COMMENT '是否自提：0-不是 1 是 DISTRIBUTE----自提',
  `send_type` tinyint(2) NULL DEFAULT 0 COMMENT '0-快递1配送2-虚拟',
  `express_no` varchar(32) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '快递单号--distribute=0时有效',
  `express_name` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '快递名称',
  `send_snap` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '送货员镜像',
  `status` varchar(20) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT 'unpaid' COMMENT '状态: unpaid 待支付,preorder-预定状态 ,init-订单初使化,ordered-订单进行中 ,paid-订单支付，但未确认支付成功,closed-订单完成,settled-组合支付状态 ,refund-退单 ,cancelled- 取消订单',
  `is_reply` tinyint(2) NULL DEFAULT 0 COMMENT '是否评论',
  `billing_id` varchar(32) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT 'orderType为union-minor时，parentId生效，为其上级合单id',
  `order_way` varchar(20) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT 'ORDER' COMMENT '该订单用于做什么 ORDER 正常订单  COPARTNET 购买合伙人',
  `pay_type` varchar(20) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '支付方式，wx,wallet',
  `merchant_id` varchar(32) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '对于平台订单,是收款店铺的shopId',
  `shop_snap` text CHARACTER SET utf8 COLLATE utf8_general_ci NULL COMMENT '店铺镜像',
  `user_id` varchar(32) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '用户Id',
  `is_vip` tinyint(2) NULL DEFAULT 0 COMMENT '是否vip订单',
  `user_snap` varchar(512) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NULL DEFAULT NULL,
  `connect_snap` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NULL DEFAULT NULL COMMENT '联系人镜像',
  `receive_side` tinyint(2) NULL DEFAULT NULL COMMENT '0--平台收款，1店铺本身收款',
  `prepay_id` varchar(50) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '微信拉起支付的prePay',
  `product_score` int(11) NULL DEFAULT 0 COMMENT '该订单商品赠送的积分',
  `deduct_score` int(11) NULL DEFAULT 0 COMMENT '抵扣积分',
  `user_coupon_id` varchar(32) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '用户使用的卡券',
  `order_snap` text CHARACTER SET utf8 COLLATE utf8_general_ci NULL,
  `cart_snap` text CHARACTER SET utf8 COLLATE utf8_general_ci NULL COMMENT '购物车镜像（productId----productSnap）',
  `address_snap` text CHARACTER SET utf8 COLLATE utf8_general_ci NULL COMMENT '配送地址',
  `cooperate_shop_id` varchar(32) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '社区团购收货点id',
  `cooperate_shop_address_snap` text CHARACTER SET utf8 COLLATE utf8_general_ci NULL COMMENT '社区团购收货点地址',
  `refund_amount` int(11) NULL DEFAULT NULL COMMENT '退单金额',
  `ref_order_ids` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '退单关联',
  `left_message` text CHARACTER SET utf8 COLLATE utf8_general_ci NULL COMMENT '买家留言',
  `show_bar_qrcode` varchar(20) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '是否生成条码，-1不生成',
  `is_good_refund` tinyint(2) NULL DEFAULT 1,
  `refund_able` tinyint(2) NULL DEFAULT 0 COMMENT '是否支持退款，1支持退单， 0不支持退单',
  `remark` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NULL DEFAULT NULL COMMENT '商家备注',
  `extend_snap` varchar(512) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL,
  `created_at` int(11) NULL DEFAULT NULL COMMENT '创建时间',
  `paid_time` int(11) NULL DEFAULT NULL COMMENT '支付时间',
  `open_time` timestamp(0) NULL DEFAULT NULL,
  `un_paid_seconds` int(11) NULL DEFAULT NULL,
  `appoint_time` int(11) NULL DEFAULT NULL COMMENT '指派时间',
  `close_time` timestamp(0) NULL DEFAULT NULL COMMENT '结单时间',
  `updated_at` int(10) NULL DEFAULT NULL,
  `order_add_snap` text CHARACTER SET utf8 COLLATE utf8_general_ci NULL COMMENT '订单加价镜像',
  `paid_user_id` varchar(32) CHARACTER SET utf8mb4 COLLATE utf8mb4_bin NULL DEFAULT NULL,
  `source_id` int(10) NULL DEFAULT NULL,
  `source_json` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL,
  PRIMARY KEY (`id`) USING BTREE
) ENGINE = InnoDB CHARACTER SET = utf8 COLLATE = utf8_general_ci COMMENT = '订单' ROW_FORMAT = Dynamic;

