DROP TABLE IF EXISTS `rf_common_auth_role`;
CREATE TABLE `rf_common_auth_role`  (
  `id` int(10) NOT NULL AUTO_INCREMENT COMMENT '主键',
  `merchant_id` int(10) UNSIGNED NULL DEFAULT 0 COMMENT '商户id',
  `shop_id` varchar(32) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NULL DEFAULT NULL COMMENT '店铺Id',
  `title` varchar(50) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NOT NULL DEFAULT '' COMMENT '标题',
  `app_id` varchar(20) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NOT NULL DEFAULT '' COMMENT '应用',
  `pid` int(10) UNSIGNED NULL DEFAULT 0 COMMENT '上级id',
  `level` tinyint(1) UNSIGNED NULL DEFAULT 1 COMMENT '级别',
  `sort` int(5) NULL DEFAULT 0 COMMENT '排序',
  `tree` varchar(300) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NOT NULL DEFAULT '' COMMENT '树',
  `status` tinyint(4) NULL DEFAULT 1 COMMENT '状态[-1:删除,0:禁用,1启用]',
  `extend_item` varchar(768) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NULL DEFAULT NULL COMMENT '扩展权限',
  `created_at` int(10) UNSIGNED NULL DEFAULT 0 COMMENT '添加时间',
  `updated_at` int(10) UNSIGNED NULL DEFAULT 0 COMMENT '修改时间',
  PRIMARY KEY (`id`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 6 CHARACTER SET = utf8mb4 COLLATE = utf8mb4_unicode_ci COMMENT = '公用_角色表' ROW_FORMAT = Dynamic;

