-- ----------------------------
-- Table structure for rf_pink
-- ----------------------------
DROP TABLE IF EXISTS `rf_pink`;
CREATE TABLE `rf_pink`  (
  `id` bigint(20) UNSIGNED NOT NULL AUTO_INCREMENT,
  `user_id` varchar(32) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '用户id，发起人Id',
  `user_name` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NULL DEFAULT NULL COMMENT '发起人名',
  `total_num` int(10) UNSIGNED NOT NULL COMMENT '购买商品个数',
  `currency_num` int(10) UNSIGNED NOT NULL DEFAULT 1 COMMENT '当前人数',
  `product_id` int(10) UNSIGNED NOT NULL COMMENT '产品id',
  `product_snap` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL,
  `end_time` int(10) NOT NULL COMMENT '结束时间',
  `is_refund` tinyint(1) UNSIGNED NULL DEFAULT 0 COMMENT '是否退款 0未退款 1已退款',
  `status` tinyint(2) NOT NULL DEFAULT 1 COMMENT '状态0进行中 1已完成-1失败',
  `is_effect` tinyint(1) UNSIGNED NULL DEFAULT NULL COMMENT '是否生效',
  `created_at` int(10) NULL DEFAULT NULL,
  `updated_at` int(10) NULL DEFAULT NULL,
  PRIMARY KEY (`id`) USING BTREE
) ENGINE = InnoDB CHARACTER SET = utf8 COLLATE = utf8_general_ci COMMENT = '拼团' ROW_FORMAT = Dynamic;

-- ----------------------------
-- Table structure for rf_pink_config
-- ----------------------------
DROP TABLE IF EXISTS `rf_pink_config`;
CREATE TABLE `rf_pink_config`  (
  `id` int(10) UNSIGNED NOT NULL AUTO_INCREMENT,
  `product_id` bigint(20) UNSIGNED NOT NULL COMMENT '产品id',
  `people` int(10) UNSIGNED NOT NULL COMMENT '拼团总人数',
  `start_time` int(10) NOT NULL COMMENT '开始时间',
  `end_time` int(10) NOT NULL COMMENT '结束时间',
  `remain_time` int(10) NULL DEFAULT NULL COMMENT '持续时间（秒）',
  `status` tinyint(2) NULL DEFAULT 1,
  `created_at` int(10) NULL DEFAULT NULL,
  `updated_at` int(10) NULL DEFAULT NULL,
  PRIMARY KEY (`id`) USING BTREE,
  UNIQUE INDEX `product`(`product_id`) USING BTREE
) ENGINE = InnoDB CHARACTER SET = utf8 COLLATE = utf8_general_ci COMMENT = '拼团配置表' ROW_FORMAT = Dynamic;

-- ----------------------------
-- Table structure for rf_pink_partake
-- ----------------------------
DROP TABLE IF EXISTS `rf_pink_partake`;
CREATE TABLE `rf_pink_partake`  (
  `id` int(10) UNSIGNED NOT NULL AUTO_INCREMENT,
  `pink_id` bigint(20) NOT NULL COMMENT '团购id',
  `user_id` varchar(32) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '参团人Id',
  `nick_name` varchar(50) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NULL DEFAULT NULL COMMENT '用户昵称',
  `head_img` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '用户头像',
  `app_open_id` varchar(32) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '小程序Id',
  `order_id` varchar(32) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '订单id 生成',
  `price` decimal(10, 2) UNSIGNED NULL DEFAULT NULL COMMENT '支付价格',
  `status` tinyint(2) UNSIGNED NOT NULL DEFAULT 0 COMMENT '状态0未支付，1已支付',
  `created_at` int(10) NULL DEFAULT NULL COMMENT '开始时间',
  `updated_at` int(10) NULL DEFAULT NULL COMMENT '结束时间',
  PRIMARY KEY (`id`) USING BTREE
) ENGINE = InnoDB CHARACTER SET = utf8 COLLATE = utf8_general_ci COMMENT = '参团人记录' ROW_FORMAT = Dynamic;

-- ----------------------------
-- Table structure for rf_print
-- ----------------------------
DROP TABLE IF EXISTS `rf_print`;
CREATE TABLE `rf_print`  (
  `id` int(11) UNSIGNED NOT NULL AUTO_INCREMENT COMMENT '收银台teminal_id',
  `disable` tinyint(2) NULL DEFAULT 0 COMMENT '是否禁用',
  `name` varchar(32) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '收银端设备名称',
  `brand` varchar(32) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '打印机品牌',
  `merchant_id` int(11) NULL DEFAULT NULL COMMENT '商户Id',
  `type` varchar(32) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '类型，库房/前台/后厨',
  `owner` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '所属人-标识',
  `sim_no` varchar(16) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '流量卡号码',
  `print_sn` varchar(20) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '云打印机编号',
  `print_key` varchar(32) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '云打印机密钥',
  `remark` varchar(512) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '备注',
  `created_at` int(10) NULL DEFAULT NULL,
  `updated_at` int(11) NULL DEFAULT NULL,
  PRIMARY KEY (`id`) USING BTREE,
  UNIQUE INDEX `sn`(`print_sn`) USING BTREE,
  INDEX `shop_id`(`disable`, `type`) USING BTREE
) ENGINE = InnoDB CHARACTER SET = utf8 COLLATE = utf8_general_ci COMMENT = '打印机' ROW_FORMAT = Dynamic;


