  `sale_price` decimal(11, 2) NULL DEFAULT NULL COMMENT '售价',
  `member_price` decimal(11, 2) NULL DEFAULT NULL COMMENT '会员价',
  `bar_code` varchar(64) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '商品条形码',
  `unit_id` varchar(32) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '单位Id',
  `unit_snap` text CHARACTER SET utf8 COLLATE utf8_general_ci NULL COMMENT '单位镜像',
  `video_cover_img` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '视频封面图',
  `images` text CHARACTER SET utf8 COLLATE utf8_general_ci NULL COMMENT '商品图片（第一张为缩略图，其他为详情）',
  `video` text CHARACTER SET utf8 COLLATE utf8_general_ci NULL COMMENT '商品视频URL地址',
  `share_img` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '商品分享图',
  `cover_img` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '商品封面图',
  `show_order` int(11) NULL DEFAULT 0,
  `product_score` int(11) NULL DEFAULT NULL COMMENT '商品赚送积分',
  `base_sales_number` int(11) NULL DEFAULT 0 COMMENT '基础出售数量',
  `real_sales_number` int(11) NULL DEFAULT 0,
  `sales_number` int(11) NULL DEFAULT 0,
  `search_code` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT 'Query商品搜索条件',
  `tips` text CHARACTER SET utf8 COLLATE utf8_general_ci NULL COMMENT '商品样式',
  `pay_type` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT '[\"wx\",\"alipay\"]' COMMENT '支付方式wx,alipay,after',
  `is_good_refund` tinyint(2) NULL DEFAULT 1,
  `command_id` int(11) NULL DEFAULT NULL COMMENT '购买后指令',
  `is_hot` tinyint(2) NULL DEFAULT 0 COMMENT '是否热销',
  `is_new` tinyint(2) NULL DEFAULT NULL COMMENT '是否新品',
  `is_on_sale` tinyint(2) NULL DEFAULT NULL COMMENT '是否上架',
  `is_sku` tinyint(2) NULL DEFAULT NULL COMMENT '商品规格：0-单规格 1多规格',
  `is_distribute` tinyint(2) NULL DEFAULT 0 COMMENT '是否独立分销',
