  `distribute_config` varchar(2048) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL,
  `support_coupon` tinyint(2) NULL DEFAULT 0 COMMENT '是否允许使用优惠券',
  `support_score` tinyint(2) NULL DEFAULT 0 COMMENT '是否允许积分抵扣',
  `is_freight` tinyint(2) NULL DEFAULT 0 COMMENT '是否另计运费',
  `freight_id` int(11) NULL DEFAULT 0 COMMENT '独立运费模版is_freight==1时有效',
  `warn_line` int(11) NULL DEFAULT NULL COMMENT '库存报警线',
  `supply_name` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '供应商名称',
  `auto_supply` tinyint(4) NULL DEFAULT NULL COMMENT '是否自动补 货',
  `supply_number` int(11) NULL DEFAULT NULL COMMENT '每次补货数量',
  `combo` tinyint(2) NULL DEFAULT 0 COMMENT '0-否1是  是否为套餐',
  `limit_number` int(11) NULL DEFAULT NULL COMMENT '预留字段',
  `weight` float(11, 2) NULL DEFAULT 0.00 COMMENT '商品重量',
  `volume` float(11, 2) NULL DEFAULT NULL COMMENT '体积',
  `single_order_limit` int(11) NULL DEFAULT NULL COMMENT '每单限购数量',
  `combo_group` text CHARACTER SET utf8 COLLATE utf8_general_ci NULL COMMENT '当combo=1时有效,本productId,组成的组合商品列表集合',
  `sale_strategy` varchar(20) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT 'NORMAL' COMMENT '商品当前生效策略',
  `thumb_count` int(11) NULL DEFAULT 0,
