-- ----------------------------
-- Table structure for rf_product_reply
-- ----------------------------
DROP TABLE IF EXISTS `rf_product_reply`;
CREATE TABLE `rf_product_reply`  (
  `id` bigint(11) UNSIGNED NOT NULL AUTO_INCREMENT COMMENT '评论ID',
  `is_recommend` tinyint(2) NULL DEFAULT 0 COMMENT '是否推存 1-是',
  `user_id` varchar(32) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '用户ID',
  `order_id` varchar(32) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '订单ID',
  `product_id` bigint(20) NOT NULL COMMENT '产品id',
  `product_snap` text CHARACTER SET utf8 COLLATE utf8_general_ci NULL COMMENT '商品镜像',
  `product_score` tinyint(5) NOT NULL COMMENT '商品分数',
  `reply_type` varchar(32) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT 'product' COMMENT '某种商品类型(普通商品、秒杀商品）',
  `service_score` tinyint(1) NULL DEFAULT NULL COMMENT '服务分数',
  `comment` varchar(512) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '评论内容',
  `pics` text CHARACTER SET utf8 COLLATE utf8_general_ci NULL COMMENT '评论图片',
  `merchant_reply_content` varchar(300) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '管理员回复内容',
  `merchant_reply_time` int(11) NULL DEFAULT NULL COMMENT '管理员回复时间',
  `is_del` tinyint(1) UNSIGNED NULL DEFAULT 0 COMMENT '0未删除1已删除   ',
  `is_reply` tinyint(1) NULL DEFAULT 0 COMMENT '0未回复1已回复',
  `status` tinyint(2) NULL DEFAULT 0,
  `show_order` int(11) NULL DEFAULT NULL COMMENT '排序',
  `created_at` int(11) NULL DEFAULT NULL COMMENT '评论时间',
  `updated_at` int(11) NULL DEFAULT NULL COMMENT '更新时间',
  PRIMARY KEY (`id`) USING BTREE,
  INDEX `add_time`(`created_at`) USING BTREE,
  INDEX `parent_id`(`reply_type`) USING BTREE,
  INDEX `is_del`(`is_del`) USING BTREE,
  INDEX `product_score`(`product_score`) USING BTREE,
  INDEX `service_score`(`service_score`) USING BTREE,
  INDEX `order_id_2`(`order_id`, `product_id`) USING BTREE
) ENGINE = InnoDB CHARACTER SET = utf8 COLLATE = utf8_general_ci COMMENT = '商品评论表' ROW_FORMAT = Dynamic;


-- ----------------------------
-- Table structure for rf_product_sku
-- ----------------------------
DROP TABLE IF EXISTS `rf_product_sku`;
CREATE TABLE `rf_product_sku`  (
  `id` varchar(32) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT 'skuId',
  `bar_code` varchar(32) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '条形码',
  `product_id` varchar(32) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '商品Id',
  `origin_price` decimal(10, 2) NULL DEFAULT NULL,
  `cost_price` decimal(11, 2) NULL DEFAULT NULL COMMENT '成本价',
  `member_price` decimal(11, 2) NULL DEFAULT NULL COMMENT '会员价',
  `sale_price` decimal(11, 2) NULL DEFAULT NULL COMMENT '售价',
  `is_sku` tinyint(2) NULL DEFAULT 0 COMMENT '是否为SKU,0-否，1 是：  ',
  `tag_ids` text CHARACTER SET utf8 COLLATE utf8_general_ci NULL,
  `tag_snap` text CHARACTER SET utf8 COLLATE utf8_general_ci NULL COMMENT '规格镜像',
  `spec_snap` text CHARACTER SET utf8 COLLATE utf8_general_ci NULL COMMENT '标签镜像',
  `stock_number` int(11) NULL DEFAULT 0 COMMENT '库存数量',
  `first_shared` decimal(11, 2) NULL DEFAULT NULL COMMENT '一级分销金额',
  `second_shared` decimal(11, 2) NULL DEFAULT NULL COMMENT '二级分销金额',
  `images` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL,
  `created_at` int(10) NULL DEFAULT NULL,
  `updated_at` int(10) NULL DEFAULT NULL,
  PRIMARY KEY (`id`) USING BTREE
) ENGINE = InnoDB CHARACTER SET = utf8 COLLATE = utf8_general_ci COMMENT = '商品SKU' ROW_FORMAT = Dynamic;

