-- Table structure for rf_product_spec
-- ----------------------------
DROP TABLE IF EXISTS `rf_product_spec`;
CREATE TABLE `rf_product_spec`  (
  `id` varchar(32) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL,
  `merchant_id` varchar(32) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL,
  `name` varchar(20) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '商品属性名称',
  `spec_tag` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '规格镜像',
  `spec_tag_detail` varchar(512) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT '0' COMMENT '规格属性',
  `show_order` int(11) NULL DEFAULT NULL COMMENT '商品属性排序',
  `created_at` int(10) NULL DEFAULT NULL,
  `updated_at` int(10) NULL DEFAULT NULL,
  PRIMARY KEY (`id`) USING BTREE
) ENGINE = InnoDB CHARACTER SET = utf8 COLLATE = utf8_general_ci COMMENT = '商品规格' ROW_FORMAT = Dynamic;

-- ----------------------------
-- Table structure for rf_product_stock
-- ----------------------------
DROP TABLE IF EXISTS `rf_product_stock`;
CREATE TABLE `rf_product_stock`  (
  `id` bigint(32) NOT NULL COMMENT '商品的skuId',
  `product_id` varchar(32) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '商品Id',
  `product_name` varchar(150) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '商品名称',
  `sku_tags` varchar(1024) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT 'sku标签',
  `sku_stock` tinyint(2) NOT NULL DEFAULT 1 COMMENT '是否sku产生的库存 ,0不是   1 是',
  `merchant_id` varchar(32) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT ' 商户Id',
  `shop_id` varchar(32) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '店铺Id',
  `total_number` int(11) NULL DEFAULT NULL COMMENT '商品总数',
  `warn_line` int(11) NULL DEFAULT NULL COMMENT '预警值',
  `profit_stock` int(11) NULL DEFAULT NULL COMMENT '期初库存',
  `input_stock` int(11) NOT NULL DEFAULT 0 COMMENT '入库数量',
  `refund_stock` int(11) NOT NULL DEFAULT 0 COMMENT '退货入库数量',
  `out_stock` int(11) NOT NULL DEFAULT 0 COMMENT '出库数量',
  `wait_out_stock` int(11) NULL DEFAULT NULL COMMENT '待出库数量',
  `wait_input_stock` int(11) NULL DEFAULT NULL COMMENT '待入库数量',
  `stock_switch` tinyint(4) NULL DEFAULT 0 COMMENT '0开启 1关闭',
  `center_stock` varchar(32) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '中央库存',
  `source_path` varchar(20) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT '0' COMMENT '平台商品：\'PLATN\',店铺商品：\'SHOP\'',
  `created_at` int(11) NULL DEFAULT NULL,
  `updated_at` int(11) NULL DEFAULT NULL,
  PRIMARY KEY (`id`) USING BTREE
) ENGINE = InnoDB CHARACTER SET = utf8 COLLATE = utf8_general_ci COMMENT = '商品库存汇总' ROW_FORMAT = Dynamic;

-- ----------------------------
-- Table structure for rf_product_stock_detail
-- ----------------------------
DROP TABLE IF EXISTS `rf_product_stock_detail`;
CREATE TABLE `rf_product_stock_detail`  (
  `id` bigint(32) UNSIGNED NOT NULL AUTO_INCREMENT,
  `sku_id` varchar(32) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL,
  `product_id` varchar(32) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '商品Id',
  `sku_stock` tinyint(2) NULL DEFAULT 0 COMMENT '是否sku产生的库存 0否， 1是',
  `spec_snap` varchar(255) CHARACTER SET utf8 COLLATE utf8_bin NULL DEFAULT NULL,
  `product_name` varchar(150) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '商品名称',
  `merchant_id` varchar(32) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL,
  `batch_number` varchar(32) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '操作批次号',
  `source_path` varchar(20) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT 'NORMAL' COMMENT '库存操作来源：收银端调用 CASHIER   系统后台调用SHOP',
  `order_id` varchar(32) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL,
  `sub_type` tinyint(2) NOT NULL DEFAULT 1 COMMENT ' 1入库2出库',
  `type` tinyint(4) NOT NULL DEFAULT 10 COMMENT '0采购入库1调拔入库2盘点入库3退货入库4期初5生产归还入库6内部令用归还7借出归还8其它入库9调拔出库10销售出库11盘点出库12锁定13生产领料14内部领用15借用出库16其它出库17报废出库,18取消回滚',
  `left_number` int(11) NULL DEFAULT NULL COMMENT '上次剩余数量',
  `stock_time` timestamp(0) NULL DEFAULT NULL COMMENT '出入库时间',
  `stock_number` int(11) NULL DEFAULT 0 COMMENT '本次操作库存数量',
  `cost_amount` int(11) NULL DEFAULT 0 COMMENT '入库合计成本金额',
  `sales_amount` int(11) NULL DEFAULT 0 COMMENT '出售合计金额',
  `order_no` varchar(32) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '关联订单Id',
  `operator` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '操作人',
  `remark` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '备注',
  `stock_json` text CHARACTER SET utf8 COLLATE utf8_general_ci NULL COMMENT '库存镜像',
  `created_at` int(11) NOT NULL DEFAULT 0,
  `updated_at` int(11) NOT NULL DEFAULT 0,
  PRIMARY KEY (`id`) USING BTREE,
  UNIQUE INDEX `uk_id`(`id`) USING BTREE,
  INDEX `ids_product_id`(`product_id`) USING BTREE,
  INDEX `ids_sku_id`(`sku_id`) USING BTREE,
  INDEX `ids_order_id`(`order_id`) USING BTREE,
  INDEX `ids_type`(`type`) USING BTREE
) ENGINE = InnoDB CHARACTER SET = utf8 COLLATE = utf8_bin COMMENT = '商品库存详情' ROW_FORMAT = Dynamic;

