-- Table structure for rf_system_group
-- ----------------------------
DROP TABLE IF EXISTS `rf_system_group`;
CREATE TABLE `rf_system_group`  (
  `id` int(11) NOT NULL AUTO_INCREMENT COMMENT '组合数据ID',
  `name` varchar(50) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL DEFAULT '' COMMENT '数据组名称',
  `type` varchar(10) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT 'NORMAL' COMMENT 'PRODUCT-商品类型 STRATEGY活动类型 NOMAL参数类型',
  `show_mod` varchar(11) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '展示方式',
  `info` varchar(256) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL DEFAULT '' COMMENT '数据提示',
  `config_name` varchar(50) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL DEFAULT '' COMMENT '数据字段',
  `spreads` text CHARACTER SET utf8 COLLATE utf8_general_ci NULL COMMENT '数据扩充 应对前端所有格式',
  `fields` text CHARACTER SET utf8 COLLATE utf8_general_ci NULL COMMENT '数据组字段以及类型（json数据）',
  `show_order` int(10) NULL DEFAULT NULL COMMENT '排序',
  `limit_number` int(4) NULL DEFAULT NULL COMMENT '限制数量',
  `created_at` int(10) NULL DEFAULT NULL,
  `updated_at` int(10) NULL DEFAULT NULL,
  PRIMARY KEY (`id`) USING BTREE,
  INDEX `config_name`(`config_name`) USING BTREE,
  INDEX `show_order`(`show_order`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 176 CHARACTER SET = utf8 COLLATE = utf8_general_ci COMMENT = '组合数据表' ROW_FORMAT = Dynamic;
